import React, { useState } from 'react';
import AlgorithmControls from './AlgorithmControls';

const SortingVisualizer = () => {
    const [array, setArray] = useState([])
    const [sortingTime, setSortingTime] = useState(0)

    const generateArray = () => {
        const newArray = []
        for (let i = 0; i < 99; i++) {
            newArray.push(Math.floor(Math.random() * 100) + 1)
        }
        setArray(newArray)
        setSortingTime(0)
    }
    const bubbleSort = async () => {
        const arr = [...array]
        const startTime = performance.now()
        for (let i = 0; i < arr.length - 1; i++) {
            for (let j = 0; j < arr.length - i - 1; j++) {
                if (arr[j] < arr[j + 1]) {
                    const tmp = arr[j];
                    arr[j] = arr[j + 1]
                    arr[j + 1] = tmp
                }
            }
            setArray([...arr]);
            await new Promise((res) => setTimeout(res, 100));
        }
        const endTime = performance.now()
        setSortingTime(endTime - startTime)
    }
    const mergeSort = async (array) => {
        if (array.length <= 1) return array;

        const mid = Math.floor(array.length / 2);
        const leftHalf = await mergeSort(array.slice(0, mid));
        const rightHalf = await mergeSort(array.slice(mid));

        return merge(leftHalf, rightHalf);
    };
    return (
        <div>
            <AlgorithmControls onGenerate={generateArray} onSort={bubbleSort} />
            <div className='sorting'>
                {array.map((value, index) => (
                    <div
                        className='bar'
                        key={index}
                        style={{
                            height: `${value * 4}px`,
                        }}
                    ></div>
                ))}
            </div>
            {sortingTime > 0 && (
                <div className="sorting-time">
                    Sorting Time: {sortingTime.toFixed(2)} ms
                </div>
            )}
        </div>
    );
};

export default SortingVisualizer;
