import React from 'react';

const AlgorithmControls = ({ onGenerate, onSort }) => {
    return (
        <nav className="navbar navbar-expand-lg navbar-light bg-light">
            <div className="container-fluid">
                <a className="navbar-brand" href="#">
                    Sorting Visualizer
                </a>
                <div className="collapse navbar-collapse" id="navbarNav">
                    <div className="navbar-nav">
                        <button
                            className="btn btn-primary me-2" // Bootstrap button styling
                            onClick={onGenerate}
                        >
                            Generate New Array
                        </button>
                        <button
                            className="btn btn-success" // Bootstrap button styling
                            onClick={onSort}
                        >
                            Bubble Sort
                        </button>
                    </div>
                </div>
            </div>
        </nav>
    );
};

export default AlgorithmControls;
